import Axios from "axios";
import React, { useContext, useEffect, useState } from "react";
import Card from "react-bootstrap/Card";
import { AppContext } from "../App";
import Button from "@mui/material/Button";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import Modal from "@mui/material/Modal";
import EditForm from "./EditForm";
import TextareaAutosize from "@mui/material/TextareaAutosize";

const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 400,
  bgcolor: "background.paper",
  border: "2px solid #000",
  boxShadow: 24,
  p: 4,
};

function Posts(props) {
  const { user } = useContext(AppContext);
  const [posts, setPosts] = useState([]);
  const id = user.id;
  const [open, setOpen] = useState(false);
  const [postEdit, setPostEdit] = useState({});
  const [editContent, setEditContent] = useState("");
  const handleOpen = (post) => {
    setOpen(true);
    setPostEdit(post);
    setEditContent(post.post_content);
  };
  const handleClose = () => setOpen(false);
  const deletePost = (postId) => {
    console.log(postId);
    Axios.get(`http://localhost:3001/delete/post/${postId}`)
      .then((response) => {
        console.log(response);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const editPost = (e) => {
    Axios.post(`http://localhost:3001/edit/post/${postEdit.id}`, {
      post_content: editContent,
    })
      .then((response) => {
        console.log(response);
      })
      .catch((err) => {
        console.log(err);
      });
    handleClose();
  };

  const editContentHandler = (e) => {
    setEditContent(e.target.value);
  };
  useEffect(() => {
    Axios.get(`http://localhost:3001/posts/${id}`).then((response) => {
      setPosts(response.data);
    });
  });

  return (
    <>
      {posts
        .slice(0)
        .reverse()
        .map((post) => (
          <Card key={post.id}>
            <Card.Header>
              {post.post_img && (
                <img
                  src={`https://res.cloudinary.com/abhi9av/image/upload/${post.post_img}.png`}
                  className="image"
                ></img>
              )}
            </Card.Header>
            <Card.Body>
              <blockquote className="blockquote mb-0">
                <p>{post.post_content}</p>
                <footer className="blockquote-footer"></footer>
                <div>
                  <Button
                    variant="outlined"
                    color="error"
                    onClick={deletePost.bind(this, post.id)}
                  >
                    Delete
                  </Button>
                  <Button
                    variant="outlined"
                    color="success"
                    onClick={handleOpen.bind(this, post)}
                  >
                    Edit
                  </Button>
                  <Modal
                    open={open}
                    onClose={handleClose}
                    aria-labelledby="modal-modal-title"
                    aria-describedby="modal-modal-description"
                  >
                    <Box sx={style}>
                      <Typography
                        id="modal-modal-title"
                        variant="h6"
                        component="h2"
                      >
                        Edit Post
                      </Typography>
                      <Typography id="modal-modal-description" sx={{ mt: 2 }}>
                        <TextareaAutosize
                          maxRows={10}
                          aria-label="maximum height"
                          placeholder="Maximum 10 rows"
                          value={editContent}
                          onChange={editContentHandler}
                          style={{ width: "100%", height: 200 }}
                        />
                      </Typography>
                      <Button
                        variant="outlined"
                        color="success"
                        onClick={editPost}
                      >
                        Edit
                      </Button>
                    </Box>
                  </Modal>
                </div>
              </blockquote>
            </Card.Body>
          </Card>
        ))}
    </>
  );
}

export default Posts;
