import * as React from "react";
import TextareaAutosize from "@mui/material/TextareaAutosize";
import { height } from "@mui/system";
import Button from "@mui/material/Button";
import { useState } from "react";
import Axios from "axios";

export default function InputPost() {
  const [image, setImage] = useState("");
  const [post, setPost] = useState("");
  const postHandler = (e) => {
    setPost(e.target.value);
  };
  const imageHandler = (e) => {
    setImage(e.target.files[0]);
  };
  Axios.defaults.withCredentials = true;
  const uploadImage = (image) => {
    const formData = new FormData();
    formData.append("file", files[0]);
    formData.append("upload_preset", "g330on9r");
    Axios.post("https://api.cloudinary.com/v1_1/abhi9av/image/upload", formData)
      .then((response) => {
        console.log(response);
      })
      .catch((err) => {
        console.log(err);
      });

  };
  const savePost = async () => {
    await Axios.post("http://localhost:3001/post", {
      post_content: post,
      post_img: null,
    })
      .then((response) => {
        console.log(response);
      })
      .catch((err) => {
        console.log(err);
      });

    if (image !== "") {
      uploadImage();
    }
  };

  return (
    <>
      <TextareaAutosize
        maxRows={10}
        aria-label="maximum height"
        placeholder="Maximum 10 rows"
        defaultValue=""
        style={{ width: 1000, height: 200 }}
        onChange={postHandler}
      />
      <input type="file" onChange={imageHandler}></input>
      <Button variant="contained" color="success" onClick={savePost}>
        Post
      </Button>
    </>
  );
}
