import React, { useState } from "react";
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import NavDropdown from "react-bootstrap/NavDropdown";
import { useEffect, useContext } from "react";
import { AppContext } from "../App";
import Button from "./Button";

function Navmenu() {
  const { loginStatus, user } = useContext(AppContext);
  let links = [
    { name: "HOME", link: "/" },
    { name: "SERVICE", link: "/" },
    { name: "ABOUT", link: "/" },
    { name: "BLOGS", link: "/" },
  ];

  const [open, setOpen] = useState(false);
  return (
    <>
      <div className="shadow-md w-full fixed top-0 left-0">
        <div className="md:flex items-center justify-between bg-white py-4 md:px-10 px-7">
          <div className="font-bold text-2xl cursor-pointer flex items-center font-[Poppins] text-gray-800">
            <span className="text-3xl text-indigo-600 mr-1">
              <ion-icon name="logo-wechat"></ion-icon>
              <h1 className="inline">Socialize</h1>
            </span>
          </div>
          <div
            onClick={() => setOpen(!open)}
            className="text-3xl absolute right-8 top-6 cursor-pointer md:hidden"
          >
            <ion-icon name={open ? "close" : "menu"}></ion-icon>
            {/* <ion-icon name="close-circle-outline"></ion-icon> */}
          </div>
          <ul
            className={`md:flex md:items-center md:pb-0 pb-12 absolute md:static bg-white md:z-auto z-[-1] left-0 w-full md:w-auto md:pl-0 pl-9 transition-all duration-500 ease-in ${open ? 'top-20' : 'top-[-500px]'}`}
          >
            {links.map((links) => (
              <li key={links.name} className="md:ml-8 text-xl md:my-0 my-7">
                <a
                  href={links.link}
                  className="text-gray-800 hover:text-gray-400 duration-500 "
                >
                  {links.name}
                </a>
              </li>
            ))}
            <Button value={"LOGIN"}></Button>
            <Button value={"REGISTER"}></Button>
          </ul>
        </div>
      </div>
    </>
  );
}

export default Navmenu;
