import { configureStore, createSlice } from "@reduxjs/toolkit";

const userSlice = createSlice({
  name: "user",
  initialState: { value: { id: "", username: "" } },
  reducers: {
    login: (state, action) => {
      state.value = action.payload;
    },

    logout: (state)=>{
        state.value
    }
  },
});

export const store = configureStore({});
