import Axios from "axios";
import React, { useContext, useEffect, useState } from "react";
import Card from "react-bootstrap/Card";
import { AppContext } from "../App";
import Button from "@mui/material/Button";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import Modal from "@mui/material/Modal";
import EditForm from "./EditForm";
import TextareaAutosize from "@mui/material/TextareaAutosize";

const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 400,
  bgcolor: "background.paper",
  border: "2px solid #000",
  boxShadow: 24,
  p: 4,
};

function Posts(props) {
  const { user } = useContext(AppContext);
  let id;
  if (props.user_id === undefined) {
    id = user.id;
  } else id = props.user_id;

  const [posts, setPosts] = useState([]);

  const [open, setOpen] = useState(false);
  const [postEdit, setPostEdit] = useState({});
  const [editContent, setEditContent] = useState("");
  const handleOpen = (post) => {
    setOpen(true);
    setPostEdit(post);
    setEditContent(post.post_content);
  };
  const handleClose = () => setOpen(false);
  const deletePost = (postId) => {
    console.log(postId);
    Axios.get(`http://localhost:3001/delete/post/${postId}`)
      .then((response) => {
        console.log(response);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const editPost = (e) => {
    Axios.post(`http://localhost:3001/edit/post/${postEdit.id}`, {
      post_content: editContent,
    })
      .then((response) => {
        console.log(response);
      })
      .catch((err) => {
        console.log(err);
      });
    handleClose();
  };

  const editContentHandler = (e) => {
    setEditContent(e.target.value);
  };
  useEffect(() => {
    Axios.get(`http://localhost:3001/posts/${id}`).then((response) => {
      setPosts(response.data);
    });
  });

  return (
    <>
    <div>

    </div>

    </>
  );
}

export default Posts;
