import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Home from "./Pages/Home";
import Navmenu from "./components/Navmenu";
import Register from "./Pages/Register";
import Login from "./Pages/Login";
import { createContext, useState, useMemo } from "react";
import Axios from "axios";

export const AppContext = createContext();

function App() {
  Axios.defaults.withCredentials = true;
  const [loginStatus, setLoginStatus] = useState(false);
  const [user, setUser] = useState("");
  const log = useMemo(
    () => ({ loginStatus, setLoginStatus }),
    [loginStatus, setLoginStatus]
  );

  useEffect(() => {
    Axios.get("http://localhost:3001/user/login").then((response) => {
      if (response.data.loggedIn === true) {
        setLoginStatus(true);
        // const payload = {
        //   id: response.data.user[0].id,
        //   username: response.data.user[0].usernmae,
        // };
        // dispatch(login({ payload }));
        // console.log(user);
      } else {
        setLoginStatus(false);
        // console.log(loginStatus);
      }
    });

    console.log(loginStatus);
    setLoginStatus(true);
  });

  return (
    <AppContext.Provider value={log}>
      <Navmenu></Navmenu>
      <Router>
        <Routes>
          <Route path="/" exact element={<Home></Home>}></Route>
          <Route path="/login" exact element={<Login></Login>}></Route>
          <Route path="/register" exact element={<Register></Register>}></Route>
        </Routes>
      </Router>
    </AppContext.Provider>
  );
}

export default App;
