import * as React from "react";
import TextareaAutosize from "@mui/material/TextareaAutosize";
import { height } from "@mui/system";
import Button from "@mui/material/Button";
import { useState } from "react";
import Axios from "axios";
import cloudinary from "cloudinary";
import { data } from "autoprefixer";

cloudinary.config({
  cloud_name: "abhi9av",
  api_key: "828325979897982",
  api_secret: "FkqFjnAtZDqEAgQn4v4SI-19W4s",
});

export default function InputPost() {
  Axios.defaults.withCredentials = true;
  const [image, setImage] = useState("");
  const [post, setPost] = useState("");
  const [imageData, setImageData] = useState({ url: "", public_id: "" });
  const postHandler = (e) => {
    setPost(e.target.value);
  };
  const imageHandler = (e) => {
    setImage(e.target.files[0]);
  };

  const uploadImage = async (image) => {
    const formData = new FormData();
    console.log(image);
    formData.append("file", image);
    formData.append("upload_preset", "g330on9r");
    formData.append("cloud_name", "abhi9av");
    // formData.append("public_id", uniq);
    try {
      const resp = Axios.post(
        `https://api.cloudinary.com/v1_1/abhi9av/image/upload`,
        formData
      );
      setImageData({
        url: resp.data.secure_url,
        public_id: resp.data.public_id,
      });
    } catch(err) {
      console.log(err);
    }

  };
  const savePost = async () => {
    if (image !== "") {
      await uploadImage(image);
    }
    await Axios.post("http://localhost:3001/post", {
      post_content: post,
      post_img: imageData.url,
    })
      .then((response) => {
        console.log(response);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <>
      <TextareaAutosize
        maxRows={10}
        aria-label="maximum height"
        placeholder="Maximum 10 rows"
        defaultValue=""
        style={{ width: 1000, height: 200 }}
        onChange={postHandler}
      />
      <input type="file" onChange={imageHandler}></input>
      <Button variant="contained" color="success" onClick={savePost}>
        Post
      </Button>
    </>
  );
}
